#pragma config(Motor,  port1,           armMotor,      tmotorVex393_HBridge, openLoop)
#pragma config(Motor,  port2,           fRightMotor,   tmotorVex393_MC29, openLoop, driveRight)
#pragma config(Motor,  port3,           rightMotor,    tmotorVex393_MC29, openLoop, reversed, driveRight)
#pragma config(Motor,  port5,           leftMotor,     tmotorVex393_MC29, openLoop, reversed, driveLeft)
#pragma config(Motor,  port7,           fLeftMotor,    tmotorVex393_MC29, openLoop, reversed, driveLeft)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

// This code is for the VEX cortex platform
#pragma platform(VEX)
// Select Download method as "competition"
#pragma competitionControl(Competition)
//Main competition background code...do not modify!
#include "Vex_Competition_Includes.c"
void pre_auton()
{
	bStopTasksBetweenModes = true;
}

task autonomous()
{
motor[fRightMotor] = 127;
motor[rightMotor] = 127;
motor[fLeftMotor] = 127;
motor[leftMotor] = 127;
wait(1.8);

motor[fRightMotor] = 0;
motor[rightMotor] = 0;
motor[fLeftMotor] = 0;
motor[leftMotor] = 0;
wait(0.5);

motor[armMotor] = -100;
wait(2);
motor[armMotor] = 100;
wait(3);

motor[fRightMotor] = -127;
motor[rightMotor] = -127;
motor[fLeftMotor] = -127;
motor[leftMotor] = -127;
wait(3);

motor[fRightMotor] = 127;
motor[rightMotor] = 127;
motor[fLeftMotor] = -127;
motor[leftMotor] = -127;
wait(2.7);

motor[fRightMotor] = 127;
motor[rightMotor] = 127;
motor[fLeftMotor] = 127;
motor[leftMotor] = 127;
wait(2);
}

task usercontrol()
{
	while (true)
	{
		//Tank Control
		//back control
		motor[leftMotor]  = vexRT[Ch3];   // Left Joystick Y value
		motor[rightMotor] = vexRT[Ch2];   // Right Joystick Y value
		//front control
		motor[fLeftMotor]  = vexRT[Ch3];   // Left Joystick Y value
		motor[fRightMotor] = vexRT[Ch2];   // Right Joystick Y value


		if(vexRT[Btn6U] == 1)       //If 6U is pressed
		{
			motor[armMotor] = 100;    //raise arm.

		}
		else if(vexRT[Btn6D] == 1)  //if 5D is pressed
		{
			motor[armMotor] = -100;   //lower arm

		}
		else                      //neither is pressed
		{
			motor[armMotor] = 0;      //hold arm

		}

	}
}
