#pragma config(Sensor, in1,    linefollow,     sensorLineFollower)
#pragma config(Motor,  port1,           flashlight,    tmotorVexFlashlight, openLoop, reversed)
#pragma config(Motor,  port2,           turntable,     tmotorServoStandard, openLoop)
#pragma config(Motor,  port3,           feedmotor,     tmotorServoStandard, openLoop)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//


// follower variable used during troubleshooting
task main()                               // main
{
	turnFlashlightOn(flashlight, -127);     // keep flashlight on throughout
	while (true)                            // while to build cycle
	{

		if (SensorValue[linefollow] > 2000 )     // if transparent
		{
			setServo(turntable, 127);           // move to the delrin hopper
		}
		else                                  // if not,
		{
			setServo(turntable, -127);           // move to the wood hopper
		}
		wait(5);                            // pause for 5 seconds

		setServo(feedmotor,-127);           // advance feed impeller
		wait(2);                            // wait 5
		setServo(feedmotor,127);             // return to original state to position for reading

	}
}
